mapper.select:
SELECT COUNT(*) AS 'count', DATE_FORMAT(wc_time,'%Y-%m-%d') AS date FROM epidemic_situation_leave WHERE stu="已审核" and DATE_FORMAT(wc_time, '%Y-%m-%d') IN
#{sevenDate}
GROUP BY DATE_FORMAT(wc_time, '%Y-%m-%d') ORDER BY DATE_FORMAT(wc_time, '%Y-%m-%d') ASC;

SELECT COUNT(*) AS 'count', DATE_FORMAT(create_time,'%Y-%m-%d') AS date FROM epidemic_situation_return WHERE status="已审核" and DATE_FORMAT(create_time, '%Y-%m-%d') IN
#{sevenDate}
GROUP BY DATE_FORMAT(create_time, '%Y-%m-%d') ORDER BY DATE_FORMAT(create_time, '%Y-%m-%d') ASC;
